/**
 * Get srcset data for a layout pattern
 * @param {string} pattern - Layout pattern (e.g., "grid(3c)")
 * @returns {string|null} Srcset value or null
 */
export function getLayoutSrcset(pattern) {
	return layoutsMap[pattern]?.srcset || null
}

/**
 * Get all data for a layout pattern
 * @param {string} pattern - Layout pattern (e.g., "grid(3c)")
 * @returns {Object|null} Layout data or null
 */
export function getLayoutData(pattern) {
	return layoutsMap[pattern] || null
}
